package com.brocode.apply;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.builder.SpringApplicationBuilder;import org.springframework.boot.web.servlet.support.SpringBootServletInitializer;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.web.WebApplicationInitializer;@Configuration@EnableAutoConfiguration@ComponentScanpublic class ApplyApplication extends SpringBootServletInitializer implements WebApplicationInitializer {  @Override  protected SpringApplicationBuilder configure(SpringApplicationBuilder application) {    return application.sources(ApplyApplication.class);  }}