package com.brocode.apply;import com.brocode.apply.buissness.utilities.CountEntitiesSerializer;import com.fasterxml.jackson.databind.ObjectMapper;import com.fasterxml.jackson.databind.module.SimpleModule;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.builder.SpringApplicationBuilder;import org.springframework.boot.web.servlet.support.SpringBootServletInitializer;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.data.util.Pair;import org.springframework.web.WebApplicationInitializer;@ComponentScan @EnableAutoConfiguration @Configurationpublic class ApplyApplication extends SpringBootServletInitializer implements WebApplicationInitializer {  @Override  protected SpringApplicationBuilder configure(SpringApplicationBuilder application) {    return application.sources(ApplyApplication.class);  }  @Bean  public ObjectMapper objectMapper() {    ObjectMapper mapper = new ObjectMapper();    SimpleModule module = new SimpleModule();    module.addSerializer(Pair.class, new CountEntitiesSerializer());    mapper.registerModule(module);    return mapper;  }}